CREATE TABLE `user_app_collect` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(45) DEFAULT NULL,  `hostname` varchar(45) CHARACTER SET utf8mb4 DEFAULT NULL,  `app` varchar(100) CHARACTER SET utf8mb4 DEFAULT NULL,  `addtime` timestamp NULL DEFAULT CURRENT_TIMESTAMP,  `updatetime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  KEY `idx_username` (`username`),  KEY `idx_app` (`app`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Mac 平台安装应用';